example.programlisting:
{- 
Return true if parity of 1s in a binary string is even 
Equivalent to Mutually Recursive Parity v1
-}
def even([]) = true
def odd([]) = false
def even("1":xs) = odd(xs)
def odd("1":xs) = even(xs)
def even("_":xs) = even(xs)
def odd("_":xs) = odd(xs)

{- 
Return true if parity of 1s in a binary string is even
Equivalent to Mutually Recursive Parity v1
-}
def even([]) = true
def even("1":xs) = odd(xs)
def even("_":xs) = even(xs)

def odd([]) = false
def odd("1":xs) = even(xs)
def odd("_":xs) = odd(xs)
